import React from "react";
import { connect } from "react-redux";
import "../../styles/Dashboard.css";
import {NavLink} from 'react-router-dom';
import { serverIp, serverPort } from '../config';
import axios from 'axios';
import {
  Card, Modal, Image,
} from 'react-bootstrap';

 class Grouptransaction extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            exp : 0,
            registeredStudents: [],
        };
        this.returnRegisteredStudents = this.returnRegisteredStudents.bind(this);

      }


      // componentDidMount() {
      //   axios.post(`${serverIp}:${serverPort}/gettransactionsdata`)
      //     .then((response) => {
      //       console.log('Response data in componentDidMount');
      //       console.log(response.data);

      //       this.setState({
      //         registeredStudents: response.data,
      //       });
      //     }).catch((err) => {
      //       console.log(`Error in componentDidMount of RegisteredStudents: ${err}`);
      //       window.alert('Error in connecting to server');
      //     });

      //   }


//     //   jobList() {
//     //     return this.state.postings.map((eachPost) => <Post post={eachPost} key={eachPost.job_post_id} />);
//     //     // for each object in exercise we are returning an Exercise component and passing three props
//     //   }

returnRegisteredStudents() {
  console.log(this.state.registeredStudents);
  return this.state.registeredStudents.map((eachStudent) => {

    return (
      <div>
        <div>
          <Card border="primary">
            <Card.Body>
              <Card.Title>

                {' '}
                {' '}
                <a href={`/StudentProfile/${eachStudent.student_id}`}>{this.capitalize(eachStudent.student_name)}</a>
              </Card.Title>
              <Card.Subtitle className="mb-2 text-muted">
                {this.capitalize(eachStudent.college_name)}
              </Card.Subtitle>
              <Card.Text>
                <b>Career Objective</b>
                {' '}
                <br />
                {eachStudent.billid}
              </Card.Text>
            </Card.Body>
          </Card>
          <br />
        </div>
        <div>
          <Modal show={this.state.show} onHide={this.handleClose}>
            <Modal.Header closeButton>
              <Modal.Title>
                {this.capitalize(eachStudent.group_name)}
                {' '}
                <Image
                  src={`${serverIp}:${serverPort}/${eachStudent.profile_picture_url}`}
                  alt="Student Profile Picture"
                  roundedCircle
                  style={{ height: 50, width: 50 }}
                />
                {' '}
                <br />

              </Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <b>College Name: </b>
              {this.capitalize(eachStudent.college_name)}
              {' '}
              <br />
              <b>Career Objective </b>
              {' '}
              <br />
              {eachStudent.descriptive}
              {' '}
              <br />
              {' '}
              <br />
              <b>Location: </b>
              {' '}
              <i>
                {this.capitalize(eachStudent.email)}
                ,
                {' '}
                {this.capitalize(eachStudent.createddate)}
                ,
                {' '}
                {this.capitalize(eachStudent.paidby)}
              </i>
              {' '}
              <br />
              {/* <b>Contact Phone: </b>
              {' '}
              <i>{eachStudent.contact_phone}</i>
              {' '}
              <br />
              <b>Contact Email: </b>
              {' '}
              <i>{eachStudent.contact_email}</i> */}
            </Modal.Body>
          </Modal>
        </div>
      </div>
    );
  });
}

render(){
   return (
    <nav className = "DashboardNav ">
     {/* {calculate(props)} */}
      <div>

        <div className="DashHeader">
          <h3>Dashboard</h3>


          <button className="btn float-right expense"  >
            Add an expense
          </button>
          <NavLink to = "/Leave"><button className = "logoutbtn">  Leave</button></NavLink>

          <NavLink to = "/GroupProfile"><button className = "logoutbtn">  Group profile</button></NavLink>
          {this.returnRegisteredStudents()}
        </div>

       </div>

    </nav>
  );
   }

}

 export default Grouptransaction;
